import React, { useState} from "react";
import CustomizedSearchBar from '../../components/atoms/CustomizedSearchBar/CustomizedSearchBar';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import OpenModal from '../../components/molecules/OpenModal/OpenModal';
import {useNavigate} from 'react-router-dom';
import HeaderWithLogout from '../../pages/Header/HeaderWithLogout';
import '../../style/style.css';

const ViewInvoicePage = ({
    onClick,
    api
}) => {
    const [invoiceData, setInvoiceData] = useState([]);
    const [invoiceFormData, setInvoiceFormData] = useState([]);
    const [searchData, setSearchData] = useState('');
    const [modalview, setModalview] = useState('');
    let navigate = useNavigate();
        // following function sets data for search (start):-
        const onHandleChangeEvent = (event) => {
            setSearchData(event.target.value);
        }
    // after entering search data and hits enter the following function will fetch invoices 
    // based on either phone number or invoice no directly (start):-
    const goEventClicked = () => {
      api.post('/delivery/getInvoices', {
        params: {
          searchData
        }
      }
      )
        .then((res) => {
          setInvoiceData(res.data);
        })
    }
    // after entering search data and hits enter the following function will fetch invoices 
    // based on either phone number or invoice no directly (end):-

    // onclick of the view invoice button the below function is called (start)
    const openinvoiceevent = (invoiceNo) => {
      api.post('/delivery/getDeliveryReportData', {
        params: {
          invoiceNo
        }
      }
      )
        .then((res) => {
          setInvoiceFormData(res.data);
        })
    }
// onclick of the view invoice button the below function is called (end)
    return (
        <div>
            <div>
          <HeaderWithLogout/>
        </div>

        <div className="viewInvoicePageContainer">
            <div className="pageheading">
                View Your Invoices
            </div>
            {/* search bar start  */}
            <CustomizedSearchBar
                onHandleChangeEvent={onHandleChangeEvent}
                type="search"
                goEventClicked={goEventClicked}
                labelname="phone number / Invoice Number"
                Btnname="search"
            />
            {/* search bar end  */}
            <div className="displayContainer">
                <div className="backarrow">
                    <p 
                        onClick={() => { 
                            navigate("/main");
                        }} 
                        ><ArrowBackIcon /></p>
                </div>
                <div className="table-responsive">
                {
                    invoiceData.length != 0 &&
                    <table className="table table-striped table-bordered">
                        <thead className="theadalter">
                            <tr>
                                <th>Invoice date</th>
                                <th>Invoice no</th>
                                <th>Customer name</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                invoiceData.map((data, i) => (
                                    <tr key={i}>
                                        <td>
                                            {
                                                data.delivery_date != null ?
                                                data.delivery_date = data.delivery_date.substring(0, 10) :
                                                data.delivery_date = data.delivery_date
                                            }
                                        </td>
                                        <td>{data.invoice_no}</td>
                                        <td>{data.customer_name}</td>
                                        <td onClick={() => { 
                                            setModalview("invoiceformat");
                                                openinvoiceevent(data.invoice_no);
                                             }}>
                                            <OpenModal
                                                api={api} 
                                                invoiceno={data.invoice_no}
                                                modalview={modalview}
                                                modalbuttonname="view invoice"
                                                invoiceFormData={invoiceFormData}
                                            />
                                        </td>
                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>
                }
                </div>
                {
                    invoiceData.length === 0 &&
                    <p> no data found! </p>
                }
            </div>
        </div>
        </div>
    );
}
export default ViewInvoicePage;
