import React, { useEffect, useState,useRef } from "react";
import CustomizedBtn from "../../atoms/CustomizedBtn/CustomizedBtn";
import { purchasesuccessmsg } from '../../organisms/SuccessMsg/SuccessMsg';
import { useReactToPrint } from "react-to-print";
import CustomizedPrint from '../../atoms/CustomizedPrint/CustomizedPrint';
import '../../../style/style.css';

const DealersDetailsForm = ({ 
  rowdata,
  handleClose,
  fetchdealersdatatoverify,
  verificationstatus,
  currentInvoiceno,
  api
}) => {
  const [updaterowdata, setUpdaterowdata] = useState();
  const [Rowdatadisplayed, setRowdatadisplayed] = useState();
  const componentRef = useRef();
    const handlePrint = useReactToPrint({
        content: () => componentRef.current,
    });
  useEffect(() => {
    fetchalldatafromcust();
}, [])
const fetchalldatafromcust = () => {
    fetch('http://localhost:3000/getalldataofcustomer', {
        method: 'post',
        headers: {
            'Content-Type': 'application/json'
        },
        mode: 'cors',
        body: JSON.stringify({
          invoice_no: currentInvoiceno
        })
    })
        .then(res => {
            return res.json()
        })
        .then(data => {
            setRowdatadisplayed(data);
        })
}
  useEffect(()=>{
    if(fetchdealersdatatoverify){
      setUpdaterowdata(fetchdealersdatatoverify);
    }
  },[fetchdealersdatatoverify])
  const [finalverificationstatus, setfinalverificationstatus] = useState();
  useEffect(()=>{
    if(verificationstatus){
      setfinalverificationstatus(verificationstatus[0].verification_status);
    }
  },[verificationstatus])
  const [constantinvoicedata, setconstantinvoicedata] = useState();

  useEffect(() => {
    if (fetchdealersdatatoverify) {
      const tempdata = [...new Set(fetchdealersdatatoverify.map((e) => e.customer_reference_no))];
      tempdata.map((temp) => {
        let filterByTempData = fetchdealersdatatoverify.filter(dataObject => dataObject.customer_reference_no === temp)
        var fixedstrings = filterByTempData[0];
        setconstantinvoicedata(fixedstrings);
      })
    }
  }, [fetchdealersdatatoverify])
  const submiteventclicked = () => {
    console.log("Rowdatadisplayed",Rowdatadisplayed);
    let custrefno = "";
    let statecode;
    Rowdatadisplayed.map((data) => {
      custrefno = data.customer_reference_no;
      statecode = data.state_code;
    })
    fetch('http://localhost:3000/setverificationstatus', {
      method: 'post',
      headers: {
        'Content-Type': 'application/json'
      },
      mode: 'cors',
      body: JSON.stringify({
        customer_reference_no: custrefno
      })
    })
      .then(res => {
        return res.json()
      })
      .then(data => {
        // console.log("d", data);
      })
      fetch('http://localhost:3000/updatepurchasestatusofdeliverytable', {
            method: 'put',
            headers: {
                'Content-Type': 'application/json'
            },
            mode: 'cors',
            body: JSON.stringify({
                customer_reference_no: custrefno
            })
        })
            .then(res => {
                return res.json()
            })
            .then(data => {
            })
      fetch('http://localhost:3000/updateDeliveryDatafromdealersform', {
            method: 'put',
            headers: {
                'Content-Type': 'application/json'
            },
            mode: 'cors',
            body: JSON.stringify({
                rowdatadisplayed: Rowdatadisplayed
            })
        })
            .then(res => {
                return res.json()
            })
            .then(data => {
            })
    fetch('http://localhost:3000/calculaterateofdeliveryofdealers', {
      method: 'post',
      headers: {
        'Content-Type': 'application/json'
      },
      mode: 'cors',
      body: JSON.stringify({
        customer_reference_no: custrefno
      })
    })
      .then(res => {
        return res.json()
      })
      .then(data => {
        // console.log("d", data);
      })
    if (statecode == 29) {
      fetch('http://localhost:3000/updatekarnatakagstratesfromdealers', {
        method: 'put',
        headers: {
          'Content-Type': 'application/json'
        },
        mode: 'cors',
        body: JSON.stringify({
          customer_reference_no: custrefno
        })
      })
        .then(res => {
          return res.json()
        })
        .then(data => {
          // console.log("d", data);
        })
      fetch('http://localhost:3000/updatefinalamountdeliveryfromdealers', {
        method: 'put',
        headers: {
          'Content-Type': 'application/json'
        },
        mode: 'cors',
        body: JSON.stringify({
          customer_reference_no: custrefno
        })
      })
        .then(res => {
          return res.json()
        })
        .then(data => {
          // console.log("d", data);
        })
      fetch('http://localhost:3000/updatebalanceamountdeliveryfromdealers', {
        method: 'put',
        headers: {
          'Content-Type': 'application/json'
        },
        mode: 'cors',
        body: JSON.stringify({
          customer_reference_no: custrefno
        })
      })
        .then(res => {
          return res.json()
        })
        .then(data => {
          const res = purchasesuccessmsg({});
          alert(res.msg);
          handleClose();
        })
    }
    else {
      fetch('http://localhost:3000/updateotherstatesgstratesfromdealers', {
        method: 'put',
        headers: {
          'Content-Type': 'application/json'
        },
        mode: 'cors',
        body: JSON.stringify({
          customer_reference_no: custrefno
        })
      })
        .then(res => {
          return res.json()
        })
        .then(data => {
          // console.log("d", data);
        })
      fetch('http://localhost:3000/updatefinalamountdeliveryfromdealers', {
        method: 'put',
        headers: {
          'Content-Type': 'application/json'
        },
        mode: 'cors',
        body: JSON.stringify({
          customer_reference_no: custrefno
        })
      })
        .then(res => {
          return res.json()
        })
        .then(data => {
          // console.log("d", data);
        })
      fetch('http://localhost:3000/updatebalanceamountdeliveryfromdealers', {
        method: 'put',
        headers: {
          'Content-Type': 'application/json'
        },
        mode: 'cors',
        body: JSON.stringify({
          customer_reference_no: custrefno
        })
      })
        .then(res => {
          return res.json()
        })
        .then(data => {
          const res = purchasesuccessmsg({});
          alert(res.msg);
          handleClose();
        })
    }
  }
  const changeevent = (event, index) => {

    let updateRowDataByIndex = [...Rowdatadisplayed];
    Rowdatadisplayed.find((item, i) => {
        if (item.delivery_id === index) {
            updateRowDataByIndex[i][event.target.name] = event.target.value;
            setRowdatadisplayed(() => [...updateRowDataByIndex]);
        }
    })
  }

  return (
    <div>
      <div><CustomizedPrint
                onClick={handlePrint}
            />
            </div>
          <div ref={componentRef}>
       <div className="formheading">
                    <h5><bold>SHREE PARAMANANDA ENTERPRISES</bold></h5>
                    <p>Shop #05,Medhini Arcade,Kithaganahalli Gate,
                        Opp Anna
                        Building,Hosur Main Road Bangalore-560099,
                        <br></br>Mobile: 9035757145/9902880851/9141070705</p>
                </div>
      <div className="pageheading">
        Purchase Order
      </div>
      <form>
        {
          Rowdatadisplayed && Rowdatadisplayed.length 
          ? Rowdatadisplayed.map((data, i) => (
            <>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    customer name:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.customer_name}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    Customer address:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.customer_address}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    Invoice no:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.invoice_no}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    GST:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.gst}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    phone number:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.phone_number}
                      type="number"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    phone number (alter one):
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.phone_number_alter_one}
                      type="number"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    phone number (alter two):
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.phone_number_alter_two}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    Product:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.product}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    quantity:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.quantity}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    rate:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.rate}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    discount:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.discount}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    cgst:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.cgst}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    sgst:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.sgst}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    igst:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.igst}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    final amount:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.final_amount}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    balance amount:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.balance_amount}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    advance amount:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.booking_advance_amount}
                      type="number"
                      onChange={(event) => changeevent(event, data.delivery_id)}
                      name="booking_advance_amount"
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    po number:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.po_number}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    vehicle number:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.vehicle_number}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="nameandinputcontainer">
                <label className="formdatalabelstyle">
                  <div className="formnamestyle">
                    pan number:
                  </div>
                  <div className="formdatainputstyle">
                    <input
                      defaultValue={data.pan_number}
                      type="text"
                      readOnly
                    />
                  </div>
                </label>
              </div>
              <div className="divider"></div>
            </>
          ))
          : <p>No Record!</p>
        }
      </form>
      </div>
      {
          finalverificationstatus == 1
          ?<p className="verificationstyle">verification completed!</p>
          :         <div className="submitcontainee">
          <CustomizedBtn
            BtnName="submit"
            onClick={submiteventclicked}
          />
        </div>
        } 
    </div>
