import React, { useEffect, useState } from "react";
import CustomizedBtn from "../../atoms/CustomizedBtn/CustomizedBtn";
import { editedsuccessmsg } from '../../organisms/SuccessMsg/SuccessMsg';
import SelectDate from "../../atoms/CustomizedDatepicker/SelectDate";
import axios from 'axios';
import '../../../style/style.css';
const CustomerFollowUpForm = ({
  rowdata,
  handleClose
}) => {
  const [updaterowdata, setUpdaterowdata] = useState();
  useEffect(() => {
    setUpdaterowdata(rowdata);
  }, [])
  const submiteventclicked = () => {
    axios.put('http://localhost:3000/customer/updateCustomerDetails',{
      params:{
        updaterowdata
      }
    })
    .then((res)=>{
      if(res){
        const res = editedsuccessmsg({});
        alert(res.msg);
        handleClose();
      }
    })
    // fetch('http://localhost:3000/updateCustomerDetails', {
    //   method: 'put',
    //   headers: {
    //     'Content-Type': 'application/json'
    //   },
    //   mode: 'cors',
    //   body: JSON.stringify(updaterowdata)
    // })
    //   .then(res => {
    //     return res.json()
    //   })
    //   .then(data => {
    //     const res = editedsuccessmsg({});
    //     alert(res.msg);
    //     handleClose();
    //   })
  }
  const changeevent = (event) => {
    setUpdaterowdata({ ...updaterowdata, [event.target.name]: event.target.value })
  }
  return (
    <div>
      <div className="pageheading">
        Edit Customer Details
      </div>
      <form>
        <div className="nameandinputcontainer">
          <label className="formdatalabelstyle">
            <div className="formnamestyle">
              Customer Reference No:
            </div>
            <div className="formdatainputstyle">
              <input
                defaultValue={rowdata.customer_reference_no}
                type="text"
                readOnly
              />
            </div>
          </label>
        </div>
        <div className="nameandinputcontainer">
          <label className="formdatalabelstyle">
            <div className="formnamestyle">
              Customer Name:
            </div>
            <div className="formdatainputstyle">
              <input
                name="customer_name"
                onChange={changeevent}
                defaultValue={rowdata.customer_name}
                type="text"
              />
            </div>
          </label>
        </div>
        <div className="nameandinputcontainer">
          <label className="formdatalabelstyle">
            <div className="formnamestyle">
              Enquiry Date:
            </div>
            <SelectDate
              type="enquiry_date"
              typeOne="enquiry_date"
              incomingdate={
                rowdata.enquiry_date != null ?
                  rowdata.enquiry_date = rowdata.enquiry_date.substring(0, 10) :
                  rowdata.enquiry_date = rowdata.enquiry_date
              }
            />
          </label>
        </div>
        <div className="nameandinputcontainer">
          <label className="formdatalabelstyle">
            <div className="formnamestyle">
              Customer Address:
            </div>
            <div className="formdatainputstyle">
              <input
                name="customer_address"
                onChange={changeevent}
                defaultValue={rowdata.customer_address}
                type="text"
              />
            </div>
          </label>
        </div>
        <div className="nameandinputcontainer">
          <label className="formdatalabelstyle">
            <div className="formnamestyle">
              Phone Number:
            </div>
            <div className="formdatainputstyle">
              <input
                name="phone_number"
                onChange={changeevent}
                defaultValue={rowdata.phone_number}
                type="number"
              />
            </div>
          </label>
        </div>
        <div className="nameandinputcontainer">
          <label className="formdatalabelstyle">
            <div className="formnamestyle">
              Enquired product hsn code:
            </div>
            <div className="formdatainputstyle">
              <input
                defaultValue={rowdata.product_hsn_code}
                type="number"
                readOnly
              />
            </div>
          </label>
        </div>
        <div className="nameandinputcontainer">
          <label className="formdatalabelstyle">
            <div className="formnamestyle">
              Enquired Product:
            </div>
            <div className="formdatainputstyle">
              <input
                defaultValue={rowdata.product}
                type="text"
                readOnly
              />
            </div>
          </label>
        </div>
        <div className="nameandinputcontainer">
          <label className="formdatalabelstyle">
            <div className="formnamestyle">
              Quantity:
            </div>
            <div className="formdatainputstyle">
              <input
                defaultValue={rowdata.quantity}
                type="number"
                readOnly
              />
            </div>
          </label>
        </div>
        <div className="nameandinputcontainer">
          <label className="formdatalabelstyle">
            <div className="formnamestyle">
              Comments:
            </div>
            <div className="formdatainputstyle">
              <input
                name="comments"
                onChange={changeevent}
                value={rowdata.comments}
                type="text"
              />
            </div>
          </label>
        </div>
        <div className="nameandinputcontainer">
          <label className="formdatalabelstyle">
            <div className="formnamestyle">
              Follow up call:
            </div>
            <SelectDate
                typeOne="requested_delivery_date"
                onHandleChangeEvent={(event) => changeevent(event, data.delivery_id)}
                incomingdate={
                    data.requested_delivery_date != null
                        ? data.requested_delivery_date = data.requested_delivery_date
                        : data.requested_delivery_date = data.requested_delivery_date
                }
            />
            <SelectDate
              typeOne="follow_up_call"
              onHandleChangeEvent={changeevent}
              incomingdate={
                rowdata.follow_up_call != null ?
                  rowdata.follow_up_call = rowdata.follow_up_call.substring(0, 10) :
                  rowdata.follow_up_call = rowdata.follow_up_call
              }
            />
          </label>
        </div>
        <div className="nameandinputcontainer">
          <label className="formdatalabelstyle">
            <div className="formnamestyle">
              Final status:
            </div>
            <div className="formdatainputstyle">
              <input
                defaultValue={rowdata.final_status}
                type="text"
                readOnly
              />
            </div>
          </label>
        </div>
        <div className="submitcontainee">
          <CustomizedBtn
            BtnName="submit"
            onClick={submiteventclicked}
          />
        </div>
      </form>
    </div>
  )
}
export default CustomerFollowUpForm;
