import React,{useState, useEffect} from "react";
import CustomizedSearchBar from "../../atoms/CustomizedSearchBar/CustomizedSearchBar";
import OpenModal from "../../molecules/OpenModal/OpenModal";
import '../../../style/style.css';

const DealersInvoice = ({
    searchData,
    onHandleChangeEvent,
    api
}) => {
    const [dealersDetails, setdealersDetails] = useState({});
    const [invoiceFormData, setInvoiceFormData] = useState([]);
    const [changedmodalview, setChangedmodalview] = useState('');
    const [formdisplaydata, setformdisplaydata] = useState('');
    // const [searchData, setSearchData] = useState('');
//      // following function sets data for search (start):-
//   const onHandleChangeEvent = (event) => {
//     setSearchData(event.target.value);
// }
    const displayDealersDetailsDatawithstatusactiveevent = () =>{
        api.get('dealers/displayDealersDetailsDatawithstatusactive')
        .then((res)=>{
            setdealersDetails(res.data);
        })
    }
    useEffect(()=>{
        displayDealersDetailsDatawithstatusactiveevent();
    },[])
    const openinvoiceevent = (gstin_number) => {
        api.post('dealers/getdealersfromgst',{
            params:{

            }
        })
        fetch('http://localhost:3000/getdealersfromgst',{
            method:'post',
            headers:{
                'Content-Type':'application/json'
            },
            mode:'cors',
            body:JSON.stringify({
                gstin_Number:gstin_number
            })
        })
        .then(res=>{
            return res.json()
        })
        .then(data=>{
            setInvoiceFormData(data);
        })
    }
    const fetchdeliverydatamatchinggstevent = (gstin_number) => {
        // console.log("testing gstin_number",gstin_number);
        fetch('http://localhost:3000/fetchdeliverymatchinggstno',{
            method:'post',
            headers:{
                'Content-Type':'application/json'
            },
            mode:'cors',
            body:JSON.stringify({
                gst:gstin_number
            })
        })
        .then(res=>{
            return res.json()
        })
        .then(data=>{
            if(data && data.length == 0){
                    data.push(
                        {'state':''})
            }
            setformdisplaydata(data);
        })
    }
    useEffect(() => {
        if (searchData === "") {
            displayDealersDetailsDatawithstatusactiveevent();
        }
    }, [searchData])
    const goEventClicked = () => {
        fetch('http://localhost:3000/getDealersDetailsData', {
            method: 'post',
            headers: {
                'Content-Type': 'application/json'
            },
            mode: 'cors',
            body: JSON.stringify({
                proprietor_name: searchData,
                proprietor_phone_number: searchData,
            })
        })
            .then(res => {
                return res.json()
            })
            .then(data => {
                setdealersDetails(data);
            })
    }
    const [dealersdata,setdealersdata] = useState();
    const dealersdataevent = (e) => {
        setdealersdata(e)
    };
    const [deliveryinvoices, setdeliveryinvoices] = useState();
    const fetchinvoicesfromdelivery = (e) => {
        fetch('http://localhost:3000/fetchinvoicesfromdelivery',{
            method:'post',
            headers:{
                'Content-Type':'application/json'
            },
            mode:'cors',
            body:JSON.stringify({
                gst:e
            })
        })
        .then(res=>{
            return res.json();
        })
        .then(data=>{
            setdeliveryinvoices(data);
        })
    }
    return(
        <div className="generateinvoicecontainer">
            <CustomizedSearchBar 
                onHandleChangeEvent={onHandleChangeEvent}
                goEventClicked={goEventClicked}
                labelname="phone number/ name" 
                Btnname="Search"
            />
            <div className='table-responsive'>
                <table className="table table-striped table-bordered ">
                    <thead className="theadalter">
                        <tr>
                            <th>GSTIN Number</th>
                            <th>Enterprise Name</th>
                            <th>Enterprise Address</th>
                            <th>Proprietor Name</th>
                            <th>Proprietor Phone Number</th>
                            <th>GST Status</th>
                            <th>Action</th>
                            <th>Generate</th>
                        </tr>
                    </thead>
                    <tbody>
                    {
                        dealersDetails && dealersDetails.length 
                        ? dealersDetails.map((data)=>(
                            <tr>
                                <td>{data.gstin_number}</td>
                                <td>{data.enterprise_name}</td>
                                <td>{data.enterprise_address}</td>
                                <td>{data.proprietor_name}</td>
                                <td>{data.proprietor_phone_number}</td>
                                <td>{data.gstin_status}</td>
                                <td 
                                    onClick={()=>{
                                            openinvoiceevent(data.gstin_number);
                                            fetchdeliverydatamatchinggstevent(data.gstin_number);
                                            setChangedmodalview("DealersInvoiceForm");
                                            dealersdataevent(data);
                                        }}
                                >
                                        <OpenModal 
                                            dealersdata={dealersdata}
                                            modalview={changedmodalview}
                                            modalbuttonname="Generate invoice" 
                                            invoiceFormData={invoiceFormData} 
                                            formdisplaydata={formdisplaydata}
                                        />
                                    </td>
                                <td
                                    onClick={()=>{
                                        // setChangedmodalview("DealersInvoiceFormGenerateFormat");
                                        setChangedmodalview("DealersInvoicesListForm");
                                        fetchdeliverydatamatchinggstevent(data.gstin_number);
                                        fetchinvoicesfromdelivery(data.gstin_number);
                                    }}
                                >
                                        <OpenModal
                                            deliveryinvoices={deliveryinvoices}
                                             modalview={changedmodalview}
                                            modalbuttonname="View invoices"
                                            invoiceFormData={formdisplaydata} 
                                        />
                                    </td>
                            </tr>
                        ))
                        :  <p>No Record!</p>
                    }
                    </tbody>
                </table>
            </div>
        </div>
    );
}
export default DealersInvoice;
