                                    </td>
                                    <td>{data.invoice_no}</td>
                                    <td>{data.customer_name}</td>
                                    <td>{data.customer_address}</td>
                                    <td>{data.gst}</td>
                                    <td>{data.phone_number}</td>
                                    <td>{data.product_hsn_code}</td>
                                    <td>{data.product}</td>
                                    <td>{data.quantity}</td>
                                    <td>{data.rate}</td>
                                    <td>{data.discount}</td>
                                    <td>{data.cgst}</td>
                                    <td>{data.sgst}</td>
                                    <td>{data.igst}</td>
                                    <td>{data.final_amount}</td>
                                    <td>{data.balance_amount}</td>
                                    <td>{data.payment_status}</td>
                                    <td>{data.booking_advance_amount}</td>
                                    <td>{data.delivery_status}</td>
                                    <td>
                                        <OpenModal
                                            displayDeliveryReportsData={displayDeliveryReportsData}
                                            rowdata={rowdata}
                                            modalview={modalview}
                                            modalbuttonname={
                                                <CustomizedEditIcon
                                                    onClick={() => { 
                                                        rowdataevent(data);
                                                        setmodalview("deliverydetailsform")
                                                     }}
                                                />
                                            }
                                        />
                                    </td>
                                </tr>
                            ))
                            : <p>No Record!</p>
                        }
                    </tbody>
                </table>
            </div>
        </div>
    );
}
export default DisplayDeliveryReportsTable;
