import React, {useEffect, useState} from 'react';
import TextField from '@mui/material/TextField';
import Autocomplete from '@mui/material/Autocomplete';
import parse from 'autosuggest-highlight/parse';
import match from 'autosuggest-highlight/match';
import Select from 'react-select';

const CustomizedComboboxForOwnerDefault = ({
  comboboxdata,
  selectevent,
  dvalue
}) => {
  const [statedata, setstatedata] = useState();
  const [defaultvalue, setdefaultvalue] = useState();
  useEffect(()=>{
    let temp = comboboxdata.map((d)=>{
      return {value:d.ownerid, label:d.ownername}
    })
    setstatedata(temp);
  },[comboboxdata])
  useEffect(()=>{
    if(dvalue){
      setdefaultvalue({label: dvalue, value: 2})
    }
    // else{
    //   setdefaultvalue({label: 'SRI PARAMANANDA ENTERPRISES', value: 2})
    // }
  },[comboboxdata, dvalue])
  const handleChange = e => {
    console.log("inside handle change of owner", e);
    setdefaultvalue(e);
    selectevent(e);
  }
  return (
    <div className='comboboxselectstyle'>
      <Select
        placeholder="Select Option"
        value={defaultvalue}
        options={statedata} // set list of the data
        onChange={handleChange} // assign onChange function
    />
  </div>
  );
}
export default CustomizedComboboxForOwnerDefault;
