import React,{useState, useEffect} from 'react';
import CustomizedEditIcon from '../../atoms/CustomizedEditIcon/CustomizedEditIcon';
import OpenModal from '../OpenModal/OpenModal';
import CustomizedSearchBar from '../../atoms/CustomizedSearchBar/CustomizedSearchBar';
import CustomizedDeleteIcon from '../../atoms/CustomizedDeleteIcon/CustomizedDeleteIcon';
import '../../../style/style.css';

const DisplayProductDetailsTable = ({
    currentstatus,
    modalview,
    api
}) => {
    const [productsdetailsdata, setProductsdetailsdata] = useState({});
    const [filterflag, setfilterflag] = useState(false);
    const [editRow, setEditRow] = useState(null);
    const [rowdata, setrowdata] = useState({});
    const [searchvalue, setSearchvalue] = React.useState('');
    const [changedmodalview, setChangedmodalview] = useState('');
    const [editFormData, setEditFormData] = useState({
        product_hsn_code:"",
        product_name:"",
        product_description:"",
        unit_of_measure:"",
        rate_per_unit:"",
        gst_rate:"",
        product_status:"",
        stock:""
    })
    useEffect(()=>{
        if(filterflag && currentstatus){
            productstatusfilter();
        }
        else {
            displayProductDetailsData();
        }
    },[currentstatus])
    const modalviewchange = () =>{
        setChangedmodalview("addproductform");
    }
    const rowdataevent = (e) => {
        setrowdata(e);
    }
    const displayProductDetailsData = () => {
        api.get('product/displayProductDetailsData')
        .then((res)=>{
            setProductsdetailsdata(data);
            setfilterflag(true);
        })
        fetch('http://localhost:3000/displayProductDetailsData',{
            method:'get',
            mode:'cors',
        })
        .then(res=>{
            return res.json()
        })
        .then(data=>{
            setProductsdetailsdata(data);
            setfilterflag(true);
        })
    }
    useEffect(()=>{
        if (searchvalue === ""){
            displayProductDetailsData();
        }
    },[searchvalue])
    const productstatusfilter = () => {
        fetch('http://localhost:3000/productstatusfilter',{
            method:'post',
            headers:{
                'Content-Type':'application/json'
            },
            mode:'cors',
            body:JSON.stringify({
                status:currentstatus
            })
        })
        .then(res => {
            return res.json()
        })
        .then(data => {
            setProductsdetailsdata(data);
        })
    }
    const editevent = (event, data ) => {
        setEditRow(data.product_hsn_code);
        const formValues = {
            product_hsn_code:data.product_hsn_code,
            product_name:data.product_name,
            product_description:data.product_description,
            unit_of_measure:data.unit_of_measure,
            rate_per_unit:data.rate_per_unit,
            gst_rate:data.gst_rate,
            product_status:data.product_status,
            stock:data.stock
        }
        setEditFormData(formValues);
    }
    const deleteevent = (id) => {
        fetch(`http://localhost:3000/deletefromproductdetailstable/${id}`,{
            method:'delete',
            headers:{
                'Content-Type':'application/json'
            },
            mode:'cors'
        })
        .then(res => {
            return res.json()
        })
        .then(data => {
            displayProductDetailsData();
        })
    }
    const searchHandle = (e) => {
        setSearchvalue(e.target.value);
      }
      const searchclicked = () => {
        fetch('http://localhost:3000/getProductDetailsData',{
            method:'post',
            headers:{
                'Content-Type':'application/json'
            },
            mode:'cors',
            body:JSON.stringify({
                product_name:searchvalue,
                product_hsn:searchvalue
            })
        })
        .then(res=>{
            return res.json()
        })
        .then(data => {
            setProductsdetailsdata(data);
        })
      }
    return(
        <div>
            <CustomizedSearchBar 
            labelname="product name / product hsn"
            Btnname="search"
            onHandleChangeEvent={searchHandle}
            goEventClicked={searchclicked}
            />
            <div className='table-responsive'>
                <form>
                    <table className='table table-striped table-bordered'>
                        <thead className='theadalter'>
                            <tr>
                                <th>Product HSN code</th>
                                <th>Product Name</th>
                                <th>Product Description</th>	
                                <th>Unit of Measure</th>
                                <th>Rate per unit</th>
                                <th>GST Rate</th>
                                <th>Product Status</th>	
                                <th>Stock</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                productsdetailsdata && productsdetailsdata.length 
                                ? productsdetailsdata.map((data)=>(
                                    <tr>
                                        <td>{data.product_hsn_code}</td>
                                        <td>{data.product_name}</td>
                                        <td>{data.product_description}</td>
                                        <td>{data.unit_of_measure}</td>
                                        <td>{data.rate_per_unit}</td>
                                        <td>{data.gst_rate}</td>
                                        <td>{data.product_status}</td>
                                        <td>{data.stock}</td>
                                        <td>
                                            <div className='editdeletecontainer'>
                                                <OpenModal 
                                                    displayProductDetailsData={displayProductDetailsData}
                                                    rowdata={rowdata}
                                                    modalview={modalview}
                                                    modalbuttonname={
                                                                    <CustomizedEditIcon 
                                                                        onClick={()=>{rowdataevent(data)}}
                                                                    />
                                                                } 
                                                />
                                                <CustomizedDeleteIcon onClick={()=>(deleteevent(data.product_hsn_code))} />
                                            </div>
                                        </td>
                                    </tr>
                                ))
                                : <p>No Record!</p>
                            }

                        </tbody>
                    </table>
                </form>
            </div>
            <OpenModal 
                    displayProductDetailsData={displayProductDetailsData}
                    modalview={changedmodalview}
                    modalbuttonname={
                                       <div
                                       onClick={modalviewchange}
                                       >
                                           Add Product
                                       </div>
                                    }
                />
        </div>
    );
}
export default DisplayProductDetailsTable;
