import React, { useState } from 'react';
import CustomizedText from '../../atoms/CustomizedText/CustomizedText';
import CustomizedDownload from '../../atoms/CustomizedDownload/CustomizedDownload';
import CustomizedDatepicker from '../../atoms/CustomizedDatepicker/CustomizedDatepicker';
import SearchIcon from '@mui/icons-material/Search';
import moment from 'moment';
const DownloadCustomer = ({setTableDataEvent}) => {
  const [fromselecteddate, fromsetselecteddate] = useState('');
  const [toselecteddate, tosetselecteddate] = useState('');
  // const [datafromcustomizeddate, setDatafromcustomizeddate] = useState([]);

  const fromselecteddateevent = (e) => {
    // console.log("from date",e);
    fromsetselecteddate(e);
  }
  const toselecteddateevent = (e) => {
    // console.log("to date",e);
    tosetselecteddate(e);
  }
  const searchbuttonclicked = () => {
    // this.CSVLink.CustomizedDownload();
    console.log("download clicked");
    console.log("from date:", fromselecteddate, "to date:", toselecteddate);
    fetch('http://localhost:3000/customizeddatefetchcustomerdata',{
      method:'post',
      headers:{
        'Content-Type':'application/json'
      },
      mode:'cors',
      body:JSON.stringify({
        fromdate:fromselecteddate,
        todate:toselecteddate
      })
    })
    .then(res=>{
      return res.json()
    })
    .then(data => {
      setTableDataEvent(data);
      // setDatafromcustomizeddate(data);
      // document.getElementById("export").click();
      // setDatafromcustomizeddate([]);
    })
  }
  const weeklysearchclicked = () => {
    console.log("weekly clicked");
    var months = {
      Jan: "01",
      Feb: "02",
      Mar: "03",
      Apr: "04",
      May: "05",
      Jun: "06",
      Jul: "07",
      Aug: "08",
      Sep: "09",
      Oct: "10",
      Nov: "11",
      Dec: "12"
    }
    var curr = new Date; // get current date
    var first = curr.getDate() - curr.getDay(); // First day is the day of the month - the day of the week
    var last = first + 6; // last day is the first day + 6

    var firstday = new Date(curr.setDate(first));
    var lastday = new Date(curr.setDate(last));

    firstday = firstday.toString();
    firstday = firstday.split(" "); 
    var fromdate = firstday[3]+"-"+months[firstday[1]]+"-"+firstday[2];

    lastday = lastday.toString();
    lastday = lastday.split(" "); 
    var todate = lastday[3]+"-"+months[lastday[1]]+"-"+lastday[2];

    console.log("download clicked");
    console.log("from date:", fromdate, "to date:", todate);
    fetch('http://localhost:3000/customizeddatefetchcustomerdata',{
      method:'post',
      headers:{
        'Content-Type':'application/json'
      },
      mode:'cors',
      body:JSON.stringify({
        fromdate:fromdate,
        todate:todate
      })
    })
    .then(res=>{
      return res.json()
    })
    .then(data => {
      setTableDataEvent(data);
    })

  }
  const monthlysearchclicked = () => {
    console.log("monthly clicked");
    var months = {
      Jan: "01",
      Feb: "02",
      Mar: "03",
      Apr: "04",
      May: "05",
      Jun: "06",
      Jul: "07",
      Aug: "08",
      Sep: "09",
      Oct: "10",
      Nov: "11",
      Dec: "12"
    }
    var curr = new Date; // get current date
    var first = curr.getDate() - curr.getDay(); // First day is the day of the month - the day of the week
    var last = first + 6; // last day is the first day + 6
    var lastday = new Date(curr.setDate(last));
    lastday = lastday.toString();
    lastday = lastday.split(" "); 
    var fromdate = lastday[3]+"-"+months[lastday[1]]+"-"+'01';
    var todate = lastday[3]+"-"+months[lastday[1]]+"-"+lastday[2];

    fetch('http://localhost:3000/customizeddatefetchcustomerdata',{
      method:'post',
      headers:{
        'Content-Type':'application/json'
      },
      mode:'cors',
      body:JSON.stringify({
        fromdate:fromdate,
        todate:todate
      })
    })
    .then(res=>{
      return res.json()
    })
    .then(data => {
      setTableDataEvent(data);
    })
  }
  const yearlysearchclicked = () => {
    console.log("yearly clicked");
    var months = {
      Jan: "01",
      Feb: "02",
      Mar: "03",
      Apr: "04",
      May: "05",
      Jun: "06",
      Jul: "07",
      Aug: "08",
      Sep: "09",
      Oct: "10",
      Nov: "11",
      Dec: "12"
    }
    var curr = new Date; // get current date
    var first = curr.getDate() - curr.getDay(); // First day is the day of the month - the day of the week
    var last = first + 6; // last day is the first day + 6
    var lastday = new Date(curr.setDate(last));
    lastday = lastday.toString();
    lastday = lastday.split(" "); 
    var fromdate = lastday[3]+"-"+"01"+"-"+'01';
    var todate = lastday[3]+"-"+months[lastday[1]]+"-"+lastday[2];

    fetch('http://localhost:3000/customizeddatefetchcustomerdata',{
      method:'post',
      headers:{
        'Content-Type':'application/json'
      },
      mode:'cors',
      body:JSON.stringify({
        fromdate:fromdate,
        todate:todate
      })
    })
    .then(res=>{
      return res.json()
    })
    .then(data => {
      setTableDataEvent(data);
    })
  }
  // console.log("customized date DATA", datafromcustomizeddate);
    return(
        <div className='fromtodownloadlinkcontainer'>
        <div className='fromtodownloadcontainer'>
          <div className='fromcontainer'>
            <CustomizedText textname="from :- " />
            <CustomizedDatepicker selecteddateevent={fromselecteddateevent} />
          </div>
          <div className='tocontainer'>
            <CustomizedText textname="to :-" />
            <CustomizedDatepicker selecteddateevent={toselecteddateevent} />
          </div>
          <SearchIcon onClick={searchbuttonclicked} />
          {/* <CustomizedDownload onClick={downloadbuttonclicked} />
          {
            datafromcustomizeddate && datafromcustomizeddate.length ?
             <CSVLink id="export" {...csvReport}>export to csv</CSVLink> : null
          } */}
        </div>
        <div>
          <span className='atagfirst'>
            <a href='#' onClick={weeklysearchclicked} >weekly</a> 
          </span>
          <span className='atagsecond'>
            <a href='#' onClick={monthlysearchclicked} >monthly</a>
          </span>
          <span className='atagthird'>
            <a href='#' onClick={yearlysearchclicked} >yearly</a>
          </span>
        </div>
        </div>

    );
}
export default DownloadCustomer;
