import React, { useEffect, useState } from "react";
import CustomizedBtn from "../../atoms/CustomizedBtn/CustomizedBtn";
import { invoicedataverificationsuccessmsg } from '../../organisms/SuccessMsg/SuccessMsg';
import SelectDate from "../../atoms/CustomizedDatepicker/SelectDate";
import '../../../style/style.css';

const CustomerInvoiceForm = ({ 
    handleClose,
    invoiceFormData,
    api
 }) => {
    const [listofinvoices, setlistofinvoices] = useState([]);
    const [finalinvoicenumber, setfinalinvoicenumber] = useState('');
    const [updaterowdata, setUpdaterowdata] = useState();
    const [temp, settemp] = useState(false);
    useEffect(() => {
        api.get('delivery/fetchinvoicesfromdeliverytable')
        .then((res)=>{
            setlistofinvoices(res.data);
        })
    }, [])
    useEffect(() => {
        var numbers = [];

        // if(listofinvoices){
        //     listofinvoices.map((data) => {
        //         if (data.invoice_no != null) {
        //             var invoicenosplit = data.invoice_no.split('/');
        //             if (invoicenosplit[1] != undefined) {
        //                 numbers.push(invoicenosplit[1]);
        //             }
        //         }
        //     })
        // }
        var maximum = Math.max(...numbers);
        var newmaximum = maximum + 1;
        if (newmaximum.toString().length === 4) {
            setfinalinvoicenumber("22-2023" + "/" + newmaximum);
        }
        else if (newmaximum.toString().length === 3) {
            setfinalinvoicenumber("22-2023" + "/" + "0" + newmaximum);
        }
        else if (newmaximum.toString().length === 2) {
            setfinalinvoicenumber("22-2023" + "/" + "00" + newmaximum);
        }
        else if (newmaximum.toString().length === 1) {
            setfinalinvoicenumber("22-2023" + "/" + "000" + newmaximum);
        }
    }, [listofinvoices])
    const submiteventclicked = (customer_reference_no) => {
        let finalInvoiceNo = '';
        let s1 = "In22-2023/";
        let min = 0;
        let max = 1000;
        let random = Math.floor(Math.random() * (max - min + 1)) + min;
        let notostring = random.toString();
        let size = notostring.length;
        if (size === 4){
            finalInvoiceNo = s1+random;
        }
        else if (size === 3){
            finalInvoiceNo = s1+"0"+random;
        }
        else if (size === 2){
            finalInvoiceNo = s1+"00"+random;
        }
        else if (size === 1){
            finalInvoiceNo = s1+"000"+random;
        }
        api.put('delivery/updateDeliveryDataafterverify',{
            params:{
                invoice_no: finalInvoiceNo,
                customer_reference_no: customer_reference_no,
                date_of_supply: updaterowdata.delivery_date,
                po_number: updaterowdata.PO_no,
                vehicle_no: updaterowdata.Vehicle_No,
                pan_number: updaterowdata.Pan_Number,
                place_of_supply: updaterowdata.Place_of_supply
            }
        })
        .then((res)=>{
            if(res){
                const res = invoicedataverificationsuccessmsg({});
                alert(res.msg);
                handleClose();
            }
        })
    }

    const changeevent = (event, index) => {
        const date = new Date();
        // const resultDate=moment(date).format('YYYY-MM-DD');
        setUpdaterowdata({...updaterowdata, [event.target.name]:event.target.value, "invoice_no":finalinvoicenumber, "delivery_date":date})
        // let updateRowDataByIndex = [...updaterowdata];
        // rowdatadisplayed.find((item, i) => {
        //     if (item.delivery_id === index) {
        //         updateRowDataByIndex[i][event.target.name] = event.target.value;
        //         setUpdaterowdata(() => [...updateRowDataByIndex]);
        //     }
        // })
    }

    // const changeevent = (event, index) => {
    //     setUpdaterowdata({...updaterowdata, [event.target.name]:event.target.value, "invoice_no":finalinvoicenumber})
    // }
    console.log("updaterowdata", updaterowdata);
    return (
        <div>
            <div className="pageheading">
                verify invoice and submit
            </div>
            {
                invoiceFormData && invoiceFormData.length &&
                invoiceFormData.map((data, i) => (
                    <form>
                        {/* Buyer details start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Buyer:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.customer_address}
                                        type="text"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Buyer detailsend */}

                        {/* State code start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    State code:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.state}
                                        type="text"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* State code end */}

                        {/* Place of supply start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Place of supply:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.place_of_supply}
                                        type="text"
                                        onChange={(event) => changeevent(event)}
                                        // onChange={(event) => changeevent(event, i)}
                                        index={i}
                                        name="Place_of_supply"
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Place of supply end  */}

                        {/*  Invoice No start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Invoice No:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.invoice_no}
                                        type="text"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Invoice No end  */}

                        {/* Date of supply start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Date of supply:
                                </div>
                                <div>
                                        <SelectDate
                                            type="booked_date"
                                            typeOne="booked_date"
                                            onHandleChangeEvent={(event) => changeevent(event)}
                                            // onHandleChangeEvent={(event)=>changeevent(event)}
                                            // onHandleChangeEvent={(event) => changeevent(event, data.delivery_id)}
                                            incomingdate={
                                                data.delivery_date != null ?
                                                    data.delivery_date = data.delivery_date :
                                                    data.delivery_date = data.delivery_date
                                            }
                                        // index={i}
                                        />
                                    </div>
                                {/* <SelectDate 
                                    typeOne="booked_date"
                                    onHandleChangeEvent={(event) => changeevent(event, i)}
                                    index={i}
                                    incomingdate={
                                        data.delivery_date != null ?
                                            data.delivery_date = data.delivery_date.substring(0, 10) :
                                            data.delivery_date = data.delivery_date
                                    }
                                /> */}
                            </label>
                        </div>
                        {/* Date of supply end  */}

                        {/* PO no start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    PO no:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        onChange={(event) => changeevent(event)}
                                        // onChange={(event) => changeevent(event, i)}
                                        index={i}
                                        defaultValue={data.po_number}
                                        type="text"
                                        name="PO_no"
                                    />
                                </div>
                            </label>
                        </div>
                        {/* PO no end  */}

                        {/* Vehicle No start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Vehicle No:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        onChange={(event) => changeevent(event)}
                                        // onChange={(event) => changeevent(event, i)}
                                        index={i}
                                        defaultValue={data.vehicle_number}
                                        type="text"
                                        name="Vehicle_No"
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Vehicle No end */}

                        {/* Pan Number start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Pan Number:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        onChange={(event) => changeevent(event)}
                                        // onChange={(event) => changeevent(event, i)}
                                        index={i}
                                        defaultValue={data.pan_number}
                                        type="text"
                                        name="Pan_Number"
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Pan Number end  */}

                        {/* Contact NO start -1 */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Contact Number - 1:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.phone_number}
                                        type="number"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Contact NO end -1 */}

                        {/* Contact NO start -2 */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Contact Number - 2:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.phone_number_alter_one}
                                        type="number"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Contact NO end  -2 */}

                        {/* Contact NO start  -3 */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Contact Number - 3:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.phone_number_alter_two}
                                        type="number"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Contact NO end  - 3*/}

                        {/* Discription of Product start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Discription of Product:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.product}
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Discription of Product end  */}

                        {/* HSN CODE start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Product HSN Code:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.product_hsn_code}
                                        type="text"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* HSN CODE end  */}

                        {/* Quantity start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Quantity:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.quantity}
                                        type="number"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Quantity end  */}

                        {/* Rate start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Rate:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.rate}
                                        type="text"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Rate end  */}

                        {/* Amount start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Amount:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.final_amount}
                                        type="number"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Amount end  */}

                        {/* Total start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Total:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.final_amount}
                                        type="number"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Total end  */}

                        {/* CGST start */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    CGST:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.cgst}
                                        type="number"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* CGST end  */}

                        {/* SGST start */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    SGST:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.sgst}
                                        type="number"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* SGST end  */}

                        {/* IGST start */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    IGST:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.igst}
                                        type="number"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* IGST end  */}

                        {/* Round off start */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Round off:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.final_amount}
                                        type="number"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Round off end  */}

                        {/* TOTAL start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    TOTAL:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.final_amount}
                                        type="number"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* TOTAL end  */}
                        {
                            data.verification_status 
                            ? <p className="verificationstyle">Invoice Data Verification Completed!</p>
                            : 
                            <div className="submitcontainee">
                                <CustomizedBtn
                                    BtnName="submit"
                                    onClick={()=>{
                                        submiteventclicked(data.customer_reference_no)
                                    }}
                                />
                            </div>
                        }
                    </form>
                ))
            }

        </div>
    )
}
export default CustomerInvoiceForm;
