import React, { useEffect, useState } from "react";
import CustomizedText from "../../atoms/CustomizedText/CustomizedText";
import CustomizedTextInput from "../../atoms/CustomizedTextInput/CustomizedTextInput";
import CustomizedBtn from "../../atoms/CustomizedBtn/CustomizedBtn";
import CustomizedReadOnlyTextInput from "../../atoms/CustomizedReadOnlyTextInput/CustomizedReadOnlyTextInput";
import { invoicedataverificationsuccessmsg } from '../../organisms/SuccessMsg/SuccessMsg';
import SelectDate from "../../atoms/CustomizedDatepicker/SelectDate";
import moment from 'moment';
import '../../../style/style.css';

const CustomerInvoiceForm = ({ handleClose, invoiceFormData, custrefno, api }) => {
    const [listofinvoices, setlistofinvoices] = useState([]);
    const [finalinvoicenumber, setfinalinvoicenumber] = useState('');
    const [rowdatadisplayed, setRowdatadisplayed] = useState();
    const [paymentstatus, setpaymentstatus] = useState(["paid", "pending"]);
    const [updatedPaymentstatus, setupdatedPaymentstatus] = useState('');
    const [bookeddate, setbookeddate] = useState('');
    const [productdata, setproductdata] = useState({});
    const [productrate, setproductrate] = useState('');
    const [cgst, setcgst] = useState('');
    const [sgst, setsgst] = useState('');
    const [igst, setigst] = useState('');
    const [finalamount, setfinalamount] = useState('');
    const [balanceamount, setbalanceamount] = useState('');
    const [updaterowdata, setUpdaterowdata] = useState();
    const [temp, settemp] = useState(false);
    useEffect(() => {
        api.get('delivery/fetchinvoicesfromdeliverytable')
        .then((res)=>{
            setlistofinvoices(res.data);
        })
    }, [])
    useEffect(() => {
        var numbers = [];

        // if(listofinvoices){
        //     listofinvoices.map((data) => {
        //         if (data.invoice_no != null) {
        //             var invoicenosplit = data.invoice_no.split('/');
        //             if (invoicenosplit[1] != undefined) {
        //                 numbers.push(invoicenosplit[1]);
        //             }
        //         }
        //     })
        // }
        var maximum = Math.max(...numbers);
        var newmaximum = maximum + 1;
        if (newmaximum.toString().length === 4) {
            setfinalinvoicenumber("22-2023" + "/" + newmaximum);
        }
        else if (newmaximum.toString().length === 3) {
            setfinalinvoicenumber("22-2023" + "/" + "0" + newmaximum);
        }
        else if (newmaximum.toString().length === 2) {
            setfinalinvoicenumber("22-2023" + "/" + "00" + newmaximum);
        }
        else if (newmaximum.toString().length === 1) {
            setfinalinvoicenumber("22-2023" + "/" + "000" + newmaximum);
        }
    }, [listofinvoices])
    const submiteventclicked = (customer_reference_no) => {
        let finalInvoiceNo = '';
        let s1 = "In22-2023/";
        let min = 0;
        let max = 1000;
        let random = Math.floor(Math.random() * (max - min + 1)) + min;
        let notostring = random.toString();
        let size = notostring.length;
        if (size === 4){
            finalInvoiceNo = s1+random;
        }
        else if (size === 3){
            finalInvoiceNo = s1+"0"+random;
        }
        else if (size === 2){
            finalInvoiceNo = s1+"00"+random;
        }
        else if (size === 1){
            finalInvoiceNo = s1+"000"+random;
        }
        api.put('delivery/updateDeliveryDataafterverify',{
            params:{
                invoice_no: finalInvoiceNo,
                customer_reference_no: customer_reference_no,
                date_of_supply: updaterowdata.delivery_date,
                po_number: updaterowdata.PO_no,
                vehicle_no: updaterowdata.Vehicle_No,
                pan_number: updaterowdata.Pan_Number,
                place_of_supply: updaterowdata.Place_of_supply
            }
        })
        .then((res)=>{
            if(res){
                const res = invoicedataverificationsuccessmsg({});
                alert(res.msg);
                handleClose();
            }
        })
    }

    const changeevent = (event, index) => {
        const date = new Date();
        // const resultDate=moment(date).format('YYYY-MM-DD');
        setUpdaterowdata({...updaterowdata, [event.target.name]:event.target.value, "invoice_no":finalinvoicenumber, "delivery_date":date})
        // let updateRowDataByIndex = [...updaterowdata];
        // rowdatadisplayed.find((item, i) => {
        //     if (item.delivery_id === index) {
        //         updateRowDataByIndex[i][event.target.name] = event.target.value;
        //         setUpdaterowdata(() => [...updateRowDataByIndex]);
        //     }
        // })
    }

    // const changeevent = (event, index) => {
    //     setUpdaterowdata({...updaterowdata, [event.target.name]:event.target.value, "invoice_no":finalinvoicenumber})
    // }
    console.log("updaterowdata", updaterowdata);
    return (
        <div>
            <div className="pageheading">
                verify invoice and submit
            </div>
            {
                invoiceFormData && invoiceFormData.length &&
                invoiceFormData.map((data, i) => (
                    <form>
                        {/* Buyer details start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Buyer:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.customer_address}
                                        type="text"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Buyer detailsend */}

                        {/* State code start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    State code:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.state}
                                        type="text"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* State code end */}

                        {/* Place of supply start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Place of supply:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.place_of_supply}
                                        type="text"
                                        onChange={(event) => changeevent(event)}
                                        // onChange={(event) => changeevent(event, i)}
                                        index={i}
                                        name="Place_of_supply"
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Place of supply end  */}

                        {/*  Invoice No start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Invoice No:
                                </div>
                                <div className="formdatainputstyle">
                                    <input
                                        defaultValue={data.invoice_no}
                                        type="text"
                                        readOnly
                                    />
                                </div>
                            </label>
                        </div>
                        {/* Invoice No end  */}

                        {/* Date of supply start  */}
                        <div className="nameandinputcontainer">
                            <label className="formdatalabelstyle">
                                <div className="formnamestyle">
                                    Date of supply:
                                </div>
                                <div>
                                        <SelectDate
                                            type="booked_date"
                                            typeOne="booked_date"
                                            onHandleChangeEvent={(event) => changeevent(event)}
                                            // onHandleChangeEvent={(event)=>changeevent(event)}
                                            // onHandleChangeEvent={(event) => changeevent(event, data.delivery_id)}
                                            incomingdate={
                                                data.delivery_date != null ?
                                                    data.delivery_date = data.delivery_date :
                                                    data.delivery_date = data.delivery_date
