import React,{useState} from "react";
import TextAndTextInput from "../../components/molecules/TextAndTextInput/TextAndTextInput";
import { useNavigate } from 'react-router-dom';
import '../../Styles/style.css';
const LoginPage = () => {
    const [logindata, setlogindata] = useState();
    let navigate = useNavigate();
    onChangeEvent = (event)=> {
        setlogindata({...logindata, [event.target.name]:event.target.value});
    }
    const onSubmitLogin = () => {
        fetch('http://localhost:3000/login',{
            method:'post',
            headers:{'Content-Type':'application/json'},
            mode:'cors',
            body:JSON.stringify({
                logincredentials:logindata
            })
        })
        .then(res => {
            return res.json()
        })
        .then(data =>{
            if (data.length >=1){
                statussetevent(true);
                navigate('/main');
            }
            else{
                alert("can't log in");
            }
        })
    }
    return(
        <div>
            {/* <div>
                <Header />
            </div> */}
            <div className="loginPageContainer">
                <div className="loginPageContainerChild">
                    <div className="usernameandpass">
                        <TextAndTextInput 
                            onChangeEvent={onChangeEvent}
                            type="username"
                            textname="USER NAME:-" />
                        <TextAndTextInput 
                            onChangeEvent={onChangeEvent}
                            type="password"
                            textname="PASSWORD:-"/>
                        </div>
                        <div className="loginPageBtnContainer">
                            <a
                                onClick={()=>{
                                    onSubmitLogin();
                                }}
                            >
                                Login
                            </a>
                        </div>
                </div>
            </div>
            {/* <div>
                <Footer />
            </div> */}
        </div>
    );
}
export default LoginPage;
