import React, { useEffect, useState, useRef } from "react";
import CustomizedBtn from '../../atoms/CustomizedBtn/CustomizedBtn';
import {purchasesuccessmsg} from '../../organisms/SuccessMsg/SuccessMsg';
import SelectDate from "../../atoms/CustomizedDatepicker/SelectDate";
import { useReactToPrint } from "react-to-print";
import CustomizedPrint from "../../atoms/CustomizedPrint/CustomizedPrint";
import CustomizedComboboxAll from "../../atoms/CustomizedCombobox/CustomizedComboboxAll";
import '../../../style/style.css';

const AddCustomersForm = ({
    currentCustomerReferenceNo,
    handleClose,
    displaycustomerfollowupevent,
    purchasestatus,
    api
}) => {
    const [rowdatadisplayed, setRowdatadisplayed] = useState();
    const [paymentstatus, setpaymentstatus] = useState(["paid", "pending"]);
    const [updatedPaymentstatus, setupdatedPaymentstatus] = useState('');
    const [bookeddate, setbookeddate] = useState('');
    const [productdata, setproductdata] = useState({});
    const [productrate, setproductrate] = useState('');
    const [cgst, setcgst] = useState('');
    const [sgst, setsgst] = useState('');
    const [igst, setigst] = useState('');
    const [finalamount, setfinalamount] = useState('');
    const [balanceamount, setbalanceamount] = useState('');
    const [state, setstate] = useState([]);
    const [statecode, setstatecode] = useState("");
    const [finalpurchasestatus, setfinalpurchasestatus] = useState();

    const componentRef = useRef();
    const handlePrint = useReactToPrint({
        content: () => componentRef.current,
    });
    useEffect(()=>{
        if(purchasestatus){
            setfinalpurchasestatus(purchasestatus[0].purchase_status);
        }
    },[purchasestatus])
    const selectevent = (event, index) => {
        let updateRowDataByIndex = [...rowdatadisplayed];
        rowdatadisplayed.find((item, i) => {
            if (item.delivery_id === index) {
                updateRowDataByIndex[i]["payment_status"] = event.target.innerHTML;
                setRowdatadisplayed(() => [...updateRowDataByIndex]);
            }
        })
    }
    const bookeddateevent = () => {
        let newDate = new Date()
        let date = newDate.getDate();
        let month = newDate.getMonth() + 1;
        let year = newDate.getFullYear();
        let finaldate = year + "-" + month + "-" + date;
        setbookeddate(finaldate);
    }
    const [updaterowdata, setUpdaterowdata] = useState();
    useEffect(() => {
        bookeddateevent();
    }, [])
    useEffect(() => {
        fetchalldatafromcust();
    }, [])
    const fetchalldatafromcust = () => {
        api.post('/delivery/getalldataofcustomer', {
            params: {
                currentCustomerReferenceNo
            }
          })
          .then((res) => {
              const data = res.data;
            setRowdatadisplayed(data);
            setUpdaterowdata(data[0]);
          })
    }

    useEffect(() => {
        if (updaterowdata && updaterowdata.product_hsn_code) {
            api.post('/product/displayProductDetailsDatabasedonproducthsn', {
                params: {
                    product_hsn_code: updaterowdata.product_hsn_code
                }
              })
              .then((res) => {
                  const data = res.data;
                  setproductdata(data);
              })
        }
    }, [updaterowdata]);

    const paymentstatusevent = (e) => {
        setupdatedPaymentstatus(e);
    }
    const submiteventclicked = () => {
        api.put('/customer/updatefinalstatuscustomertable', {
            params: {
                final_status: "Booked",
                customer_reference_no: currentCustomerReferenceNo
            }
          })
          .then((res) => {
            displaycustomerfollowupevent();
          })
        api.put('/delivery/updatepurchasestatusofdeliverytable', {
            params: {
                customer_reference_no: currentCustomerReferenceNo
            }
          })
          .then((res) => {
            displaycustomerfollowupevent();
          })
        api.put('/delivery/updateDeliveryData', {
            params: {
                rowdatadisplayed: rowdatadisplayed
            }
          })
          .then((res) => {
            displaycustomerfollowupevent();
          })


        fetch('http://localhost:3000/updateDeliveryData', {
            method: 'put',
            headers: {
                'Content-Type': 'application/json'
            },
            mode: 'cors',
            body: JSON.stringify({
                rowdatadisplayed: rowdatadisplayed
            })
        })
            .then(res => {
                return res.json()
            })
            .then(data => {
            })
        fetch('http://localhost:3000/updaterateofdelivery', {
            method: 'put',
            headers: {
                'Content-Type': 'application/json'
            },
            mode: 'cors',
            body: JSON.stringify({
                rowdatadisplayed: rowdatadisplayed
            })
        })
            .then(res => {
                return res.json()
            })
            .then(data => {
            })

        fetch('http://localhost:3000/getstatecodefromdelivery', {
            method: 'post',
            headers: {
                'Content-Type': 'application/json'
            },
            mode: 'cors',
            body: JSON.stringify({
                rowdatadisplayed: rowdatadisplayed
            })
        })
            .then(res => {
                return res.json()
            })
            .then(data => {
                for (var i = 0; i < data.length; i++) {
                    if (data[i].state_code === 29) {
                        fetch('http://localhost:3000/updatekarnatakagstrates', {
                            method: 'put',
                            headers: {
                                'Content-Type': 'application/json'
                            },
                            mode: 'cors',
                            body: JSON.stringify({
                                customer_reference_no: data[i].customer_reference_no,
                                product_hsn_code: data[i].product_hsn_code
                            })
                        })
                            .then(res => {
                                return res.json();
                            })
                            .then(data => {
                            })
                        fetch('http://localhost:3000/updatefinalamountdelivery', {
                            method: 'put',
                            headers: {
                                'Content-Type': 'application/json'
                            },
                            mode: 'cors',
                            body: JSON.stringify({
                                rowdatadisplayed: rowdatadisplayed
                            })
                        })
                            .then(res => {
                                return res.json()
                            })
                            .then(data => {
                            })
                        fetch('http://localhost:3000/updatebalanceamountdelivery', {
                            method: 'put',
                            headers: {
                                'Content-Type': 'application/json'
                            },
                            mode: 'cors',
                            body: JSON.stringify({
                                rowdatadisplayed: rowdatadisplayed
                            })
                        })
                            .then(res => {
                                return res.json()
                            })
                            .then(data => {
                                const res = purchasesuccessmsg({});
                                alert(res.msg);
                                handleClose();
                            })
                    }
                    else {
                        fetch('http://localhost:3000/updateotherstatesgstrates', {
                            method: 'put',
                            headers: {
                                'Content-Type': 'application/json'
                            },
                            mode: 'cors',
                            body: JSON.stringify({
                                customer_reference_no: data[i].customer_reference_no,
                                product_hsn_code: data[i].product_hsn_code
                            })
                        })
                            .then(res => {
                                return res.json();
                            })
                            .then(data => {
                            })
                        fetch('http://localhost:3000/updatefinalamountdelivery', {
                            method: 'put',
                            headers: {
                                'Content-Type': 'application/json'
                            },
                            mode: 'cors',
                            body: JSON.stringify({
                                rowdatadisplayed: rowdatadisplayed
                            })
                        })
                            .then(res => {
                                return res.json()
                            })
                            .then(data => {
                            })
                        fetch('http://localhost:3000/updatebalanceamountdelivery', {
                            method: 'put',
                            headers: {
                                'Content-Type': 'application/json'
                            },
                            mode: 'cors',
                            body: JSON.stringify({
                                rowdatadisplayed: rowdatadisplayed
                            })
                        })
                            .then(res => {
                                return res.json()
                            })
                            .then(data => {
                                const res = purchasesuccessmsg({});
                                alert(res.msg);
                                handleClose();
                            })
                    }
                }

            })
    }
    const changeevent = (event, index) => {
        let updateRowDataByIndex = [...rowdatadisplayed];
        rowdatadisplayed.find((item, i) => {
            if (item.delivery_id === index) {
                updateRowDataByIndex[i][event.target.name] = event.target.value;
                setRowdatadisplayed(() => [...updateRowDataByIndex]);
            }
        })
    }
    return (
        <div>
            <div><CustomizedPrint
                onClick={handlePrint}
            />
            </div>
            <div ref={componentRef}>
                <div className="formheading">
                    <h5><bold>SHREE PARAMANANDA ENTERPRISES</bold></h5>
                    <p>Shop #05,Medhini Arcade,Kithaganahalli Gate,
                        Opp Anna
                        Building,Hosur Main Road Bangalore-560099,
                        <br></br>Mobile: 9035757145/9902880851/9141070705</p>
                </div>
                <div className="pageheading">
                    Purchase Order
                </div>
                {
                    rowdatadisplayed && rowdatadisplayed.length &&
                    rowdatadisplayed.map((data, i) => (
                        <form>
                            {/* customer reference no start  */}
                            <div className="nameandinputcontainer">
                                <label className="formdatalabelstyle">
                                    <div className="formnamestyle">
                                        Customer Reference Number:
                                    </div>
                                    <div className="formdatainputstyle">
                                        <input
                                            defaultValue={currentCustomerReferenceNo}
                                            type="text"
                                            readOnly
                                        />
                                    </div>
                                </label>
                            </div>
                            {/* customer reference no end */}
                            {/* Booked date start  */}
                            <div className="nameandinputcontainer">
                                <label className="formdatalabelstyle">
                                    <div className="formnamestyle">
                                        Booked date:
                                    </div>
                                    <div>
                                        <SelectDate
                                            type="booked_date"
                                            typeOne="booked_date"
                                            onHandleChangeEvent={(event) => changeevent(event, data.delivery_id)}
                                        // index={i}
                                        />
                                    </div>
                                </label>
                            </div>
                            {/* Booked date end  */}
                            {/* Requested delivery date start  */}
                            <div className="nameandinputcontainer">
                                <label className="formdatalabelstyle">
                                    <div className="formnamestyle">
                                        Requested delivery date:
                                    </div>
                                    <div>
                                        <SelectDate
                                            typeOne="requested_delivery_date"
                                            // onHandleChangeEvent={changeevent}
                                            onHandleChangeEvent={(event) => changeevent(event, data.delivery_id)}
                                            // index={i}
                                            incomingdate={
                                                data.requested_delivery_date != null
                                                    ? data.requested_delivery_date = data.requested_delivery_date
                                                    : data.requested_delivery_date = data.requested_delivery_date
                                            }
                                        />
                                    </div>
                                </label>
                            </div>
                            {/* Requested delivery date end */}

                            {/* Customer Address start  */}
                            <div className="nameandinputcontainer">
                                <label className="formdatalabelstyle">
                                    <div className="formnamestyle">
                                        Customer Address:
                                    </div>
                                    <div className="formdatainputstyle">
                                        <input
                                            defaultValue={data.customer_address}
                                            name="customer_address"
                                            type="text"
                                            onChange={(event) => changeevent(event, data.delivery_id)}
                                        // index={i}
                                        />
                                    </div>
                                </label>
                            </div>
                            {/* Customer Address end  */}

                            {/*  state start  */}
                            <div className="nameandinputcontainer">
                                <label className="formdatalabelstyle">
                                    <div className="formnamestyle">
                                        State:
                                    </div>
                                    <div className="formdatainputstyle">
                                        <input
                                            defaultValue={data.state}
                                            type="text"
                                            readOnly
                                        />
                                    </div>
                                </label>
                            </div>
                            {/* state end  */}

                            {/* Phone number one start  */}
                            <div className="nameandinputcontainer">
                                <label className="formdatalabelstyle">
                                    <div className="formnamestyle">
                                        Phone number:
                                    </div>
                                    <div className="formdatainputstyle">
                                        <input
                                            type="number"
                                            // onHandleChangeEvent={(event) => changeevent(event, i)}
                                            // index={i}
                                            name="phone_number"
                                            defaultValue={data.phone_number}
                                        />
                                    </div>
                                </label>
                            </div>
                            {/* Phone number one end  */}

                            {/* Product hsn code start   */}
                            <div className="nameandinputcontainer">
                                <label className="formdatalabelstyle">
                                    <div className="formnamestyle">
                                        Product hsn code:
                                    </div>
                                    <div className="formdatainputstyle">
                                        <input
                                            type="number"
                                            defaultValue={data.product_hsn_code}
                                        />
                                    </div>
                                </label>
                            </div>
                            {/* Product hsn code end  */}

                            {/* Product start  */}
                            <div className="nameandinputcontainer">
                                <label className="formdatalabelstyle">
                                    <div className="formnamestyle">
                                        Product:
                                    </div>
                                    <div className="formdatainputstyle">
                                        <input
                                            type="text"
                                            defaultValue={data.product}
                                            readOnly
                                        />
                                    </div>
                                </label>
                            </div>
                            {/* Product end */}

                            {/* Quantity start  */}
                            <div className="nameandinputcontainer">
                                <label className="formdatalabelstyle">
                                    <div className="formnamestyle">
                                        Quantity:
                                    </div>
                                    <div className="formdatainputstyle">
                                        <input
                                            type="number"
                                            name="quantity"
                                            defaultValue={data.quantity}
                                        // onHandleChangeEvent={(event) => changeevent(event, i)}
                                        // index={i}
                                        />
                                    </div>
                                </label>
                            </div>
                            {/* Quantity end  */}

                            {/* Product Rate start  */}
                            <div className="nameandinputcontainer">
                                <label className="formdatalabelstyle">
                                    <div className="formnamestyle">
                                        Product Rate:
                                    </div>
                                    <div className="formdatainputstyle">
                                        <input
                                            type="text"
                                            defaultValue={data.rate}
                                            readOnly
                                        />
                                    </div>
                                </label>
                            </div>
                            {/* Product Rate end  */}

                            {/* Discount start  */}
                            <div className="nameandinputcontainer">
                                <label className="formdatalabelstyle">
                                    <div className="formnamestyle">
                                        Discount:
